  function LootsList({ endpointSuffix, selectedProduct, selectedType }) {
    const { loots, loading, error } = useLoots(endpointSuffix);

    if (loading) return <div>Загрузка...</div>;
    if (error) return <div>Тут пусто...</div>;

    const filteredLoots = loots.filter(loot => {
        // Проверяем, что товар не продан
        const isNotSold = endpointSuffix === '' ? loot.status !== 'sold' : true;
        // Фильтруем по продукту, если он выбран
        const matchesProduct = !selectedProduct || loot.productName === selectedProduct;
        // Фильтруем по типу, если он выбран
        const matchesType = !selectedType || loot.type === selectedType;
        return isNotSold && matchesProduct && matchesType;
    });

    return (
        <>
            {filteredLoots.length === 0 && <h3 style={{paddingTop: '5em'}}><b>Тут пусто...</b></h3>}
            {filteredLoots.length > 0 && (
                <table className='table-custom'>
                    <thead>
                        <tr>
                            <th>Сервис</th>
                            <th>Тип</th>
                            <th>Название</th>
                            <th>Цена</th>
                            <th>Описание товара</th>
                            <th>Продавец</th>
                            if({endpointSuffix==='my'} {
                                <th>Редактировать товар</th>
                            })
                        </tr>
                    </thead>
                    <tbody>
                        {filteredLoots.map(loot => (
                            <LootRow key={loot.id} loot={loot} svg={productSVGs[loot.productName]} />
                        ))}
                    </tbody>
                </table>
            )}
        </>
    );
}

export default LootsList;
